import SimpleInput from './components/SimpleInput';
import WorkBook from './components/WorkBook';
import { useState, useEffect, useCallback } from 'react';

function App() {
  const [entries, setData] = useState([]);
  // https://react-workbook-default-rtdb.firebaseio.com/entries.json
  const fetchEmployeeHandler = useCallback(async () => {
    const response = await fetch('https://react-workbook-default-rtdb.firebaseio.com/entries.json')
    const data = await response.json();

    const transformedData = data.map((employeeData) => {
      return {
        id: employeeData.id,
        fname: employeeData.name,
        lname: employeeData.lname,
        email: employeeData.email,
        birthdate: employeeData.birthdate
      };
    });
    setData(transformedData);

  }, []);

  useEffect(() => {
    fetchEmployeeHandler();
  }, [fetchEmployeeHandler]);

  async function addEntryHandler(entry) {
    const response = await fetch('https://react-workbook-default-rtdb.firebaseio.com/entries.json', {
      method: 'POST',
      body: JSON.stringify(entry),
      headers: {
        'Content-Type': 'application/json'
      }
    });
    const data = await response.json();
    console.log(data)
  }

  return (
    <div className="app">
      <SimpleInput onAddEntry={addEntryHandler} />

      <section>
        <button onClick={fetchEmployeeHandler}>Fetch Entries</button>
      </section>
      <section>
        <WorkBook entries={entries} />
      </section>
    </div>
  );
}

export default App;
