import SimpleInput from './components/SimpleInput';
import WorkBook from './components/WorkBook';
import {useState } from 'react';

function App() {
  const [entries, setData] = useState([]);

  function fetchEmployeeHandler() {
    fetch('https://api.jsonbin.io/b/615162194a82881d6c56287b')
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        const transformedData = data.results.map((employeeData) => {
          return {
            eid: employeeData.eid,
            fname: employeeData.fname,
            lname: employeeData.lname,
            email: employeeData.email,
            birthdate: employeeData.birthdate
          };
        });
        setData(transformedData);
      });
  }
  return (
    <div className="app">
      <SimpleInput />

      <section>
        <button onClick={fetchEmployeeHandler}>Fetch Entries</button>
      </section>
      <section>
        <WorkBook entries={entries} />
      </section>
    </div>
  );
}

export default App;
