import React, { useState } from "react";

const useNumber = (input) => {
    const [enteredValue, setEnteredValue] = useState('');
    const [enteredValueIsValid, setEnteredValueIsValid] = useState(false);
    const [isTouched, setIsTouched] = useState(false);

    const numberRegEx = /[0-9]/;

    const valueIsValid = input(numberRegEx.test(enteredValue))
    const hasError = !valueIsValid && isTouched;
    const result = numberRegEx.test(enteredValue)

    const valueChangeHandler = event => {
        setEnteredValue(event.target.value);
        
        let n = event.target.value.length;

        if (event.target.value === input(numberRegEx.test(enteredValue))){
            setEnteredValueIsValid(false)
        }
    };

    const inputBlurHandler = event => {
        console.log(result)
        setIsTouched(true);
    };

    const reset = () => {
        setEnteredValue('');
        setIsTouched(false);
    };

    return {
        value: enteredValue,
        isValid: valueIsValid,
        hasError,
        valueChangeHandler,
        inputBlurHandler,
        reset
    };
};

export default useNumber;
